                        target: dataObjTarget[i] && dataObjTarget[i][j],
                        showvalue : dataObjShowValues[i] && dataObjShowValues[i][j],
                        tooltext : dataObjToolTexts[i] && dataObjToolTexts[i][j] &&
                            dataObjToolTexts[i][j].replace(/\\/g, ''),
